package main

import (
  "fmt"
  "strings"
)

func getBathroomCode(instructions string) {
  position := []int{1,1}
  lines := strings.Split(instructions, "\n")
  for _,line := range lines {
    instructionSet := strings.Split(line, "")
    for _,instruction := range instructionSet {
      switch instruction {
        case "U": if (position[0] - 1) >= 0 { position[0]-- }
        case "L": if (position[1] - 1) >= 0 { position[1]-- }
        case "D": if (position[0] + 1) < 3 { position[0]++ }
        case "R": if (position[1] + 1) < 3 { position[1]++ }
      }
    }
    fmt.Print(position[0] * 3 + position[1] + 1)
  }
  fmt.Println()
}

func main() {
  // Sample Input
  instructions := 
  `ULL
RRDDD
LURDL
UUUUD`
  getBathroomCode(instructions)
  
  // Puzzle Input
  instructions = 
  `



`
  getBathroomCode(instructions)
}
